import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
// import { FoodService } from './food.service';


@Component({
  selector: 'app-fetch-ChatGPT-data',
  templateUrl: './fetch-ChatGPT-data.component.html',
  styleUrls: ['./fetch-ChatGPT-data.component.css'],
})


export class FetchChatDataComponent {

  public APIResponse: string = '';
  public SearchText = '';

  constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<ChatGPTRequestDTO[]>(baseUrl + 'api/chatgpt').subscribe(result => {
      this.APIResponse += result;
    }, error => console.error(error));
  }


  onSubmit() {
    const requestPayload = {
      SearchText: this.SearchText // Include user input in the request
    };
    console.log(requestPayload.SearchText)

    this.http.post<any>('https://localhost:44417/api/chatgpt', requestPayload).subscribe(response => {
    console.log(response.answer)
    this.APIResponse = response.answer;
    }, error => console.error(error));
  }

  public formatResponse(response: string): string {
    return response.replace(/\n/g, '<br>');
  }
  displayStyle = "none";
  
  openPopup() {
    this.displayStyle = "block";
  }
  closePopup() {
    this.displayStyle = "none";
  }



}


interface ChatGPTRequestDTO {
  response: string;
}


<br>
<br>
<!-- <p *ngIf="!APIResponse?.length"><em>Loading...</em></p> -->
<!-- <my-child></my-child> -->

<br>

<form (submit)="onSubmit()">
  <h5>Insert ingredients</h5>
  <input [(ngModel)]="SearchText" name="ingredients">
  <button type="submit">Search Recipe</button>
</form>
<br>
<h6>Output</h6>





<h3>Please click on the below button open popup</h3>
<hr />
<button
  style="margin: 50px; padding: 10px"
  type="button"
  class="btn btn-primary"
  (click)="openPopup()">Show Data
</button>
<div
  class="modal"
  tabindex="-1"
  role="dialog"
  [ngStyle]="{'display':displayStyle}">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h4 class="modal-title">GeeksForGeeks</h4>
      </div>
      <div class="modal-body">
        <p>One Stop Solution for all CS problems</p>
  
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-danger" 
                (click)="closePopup()">
          Close
        </button>
      </div>
    </div>
  </div>
</div>



<!-- Button trigger modal -->
<button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalLong">
  Launch demo modal
</button>

<!-- Modal -->
<div class="modal fade" id="exampleModalLong" tabindex="-1" role="dialog" aria-labelledby="exampleModalLongTitle" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
