import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';




@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-ChatGPT-data.component.html'
})

export class FetchChatDataComponent {

  public food = [];
  // onChange() {
  //   this.http.get<any>('./db/generic_food').subscribe(result => {
  //     console.log(result)
  //     this.food = result

  //   })

  genericFood = ('./db/generic_food.csv');



  getinfo() {
    return this.http.get(this.genericFood, {responseType: 'text'})
  }







  public APIResponse: ChatGPTRequestDTO[] = [];

  public SearchText = '';

  constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<ChatGPTRequestDTO[]>(baseUrl + 'api/chatgpt').subscribe(result => {
      this.APIResponse = result;
    }, error => console.error(error));
  }


  onSubmit() {
    const requestPayload = {
      SearchText: this.SearchText // Include user input in the request
    };
    console.log(requestPayload.SearchText)

    this.http.post<any>('https://localhost:44417/api/chatgpt', requestPayload).subscribe(response => {
    console.log(response.answer)
    this.APIResponse = response.answer;
    }, error => console.error(error));
  }
}


interface ChatGPTRequestDTO {
  response: string;
}
<h1 id="tableLabel">ChatGPT</h1>

<br>

<!-- <p *ngIf="!APIResponse?.length"><em>Loading...</em></p> -->
<form>
  <input (submit)="onChange" placeholder="{{food}}">
  <button type="submit" >Submit</button>
</form>
<br>
<br>
<br>

<form (submit)="onSubmit()">
  <h5>Insert ingredients</h5>
  <input [(ngModel)]="SearchText" name="ingredients">
  <button type="submit">Search Recipe</button>
</form>
<br>
<h6>Output</h6>

<div id="output">
<div id="output" >
  <p id="response">{{ APIResponse }}</p>
</div>
</div>

<style>
  h6 {
    color:red;
  }
  #output {
    background-color: azure;
    height: 50px;
    width: 100%;
    border-color: blue;
    border-style: double;
  }
</style>
