import { Component, Inject, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FoodService } from './food.service';


@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-ChatGPT-data.component.html',
  providers: [FoodService]
})


export class FetchChatDataComponent implements OnInit{
  searchTerm: string = '';
  options: string[] = []; // Your options array from the service
  selectedOptions: string[] = [];
  filteredOptions: { name: string, options: string[] }[] = [];

    constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string, private foodService:FoodService) {
      http.get<ChatGPTRequestDTO[]>(baseUrl + 'api/chatgpt').subscribe(result => {
      this.APIResponse = result;
    }, error => console.error(error));
  }

  foodData: any[] = [];

  ngOnInit() {
    this.getData();
  }

  getData() {
    this.foodService.getInfo().subscribe(data => {
      const rows = data.split('\n');
      const firstColumn = rows.slice(1)
        .map(row => row.split(',')[0].trim())
        .filter(value => value !== '');
  
      console.log(firstColumn);
    });
  }

  filterOptions() {
    const filteredSections = this.options.filter(option =>
      option.toLowerCase().includes(this.searchTerm.toLowerCase())
    );

    this.filteredOptions = [];
    let currentSection: { name: string, options: string[] } | null = null;

    for (const option of filteredSections) {
      const firstLetter = option.charAt(0).toUpperCase();
      if (!currentSection || currentSection.name !== firstLetter) {
        currentSection = { name: firstLetter, options: [] };
        this.filteredOptions.push(currentSection);
      }
      currentSection.options.push(option);
    }

    this.selectedOptions = filteredSections;
  }






  public APIResponse: ChatGPTRequestDTO[] = [];

  public SearchText = '';

  // constructor(private http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
  //   http.get<ChatGPTRequestDTO[]>(baseUrl + 'api/chatgpt').subscribe(result => {
  //     this.APIResponse = result;
  //   }, error => console.error(error));
  // }


  onSubmit() {
    const requestPayload = {
      SearchText: this.SearchText // Include user input in the request
    };
    console.log(requestPayload.SearchText)

    this.http.post<any>('https://localhost:44417/api/chatgpt', requestPayload).subscribe(response => {
    console.log(response.answer)
    this.APIResponse = response.answer;
    }, error => console.error(error));
  }
}


interface ChatGPTRequestDTO {
  response: string;
}
