import { Component } from '@angular/core';
import { FoodService } from '../food-search/food.service';

@Component({
  selector: 'app-food-search',
  templateUrl: './food-search.component.html',
  styleUrls: ['./food-search.component.css'],
  providers: [FoodService],
  template: '<h1>Hello World!</h1>'
})


export class FoodSearchComponent {

  constructor( private foodService:FoodService) {

  }

  foods: any[] = [];
  selectedFood: any[] = [];
  searchContext = '';   

  // ngOnInit() {
  //   console.log('I want a recipe with the following ingredients: ')
  // }

  getData(startingLetters: string) {
    console.log(startingLetters)
    this.foodService.getInfo().subscribe(data => {
      this.foods = data
      .filter(food => food.FOOD_NAME.trim().toLowerCase().startsWith(startingLetters))
      .slice(0, 10);

      if (startingLetters === '') {
        this.foods = [];
      }
    });
  }


  onKeyPressMove(event: KeyboardEvent) {
    const pressedKey = event.key.toLowerCase();

    if ((event.keyCode < 65 || event.keyCode > 90) && event.keyCode !== 8) {
      event.preventDefault();
    }
    else if (pressedKey.length > 1) {
      this.searchContext = this.searchContext.slice(pressedKey.length - pressedKey.length, -1);
    }
    else {
      this.searchContext += pressedKey;
    }
    this.getData(this.searchContext);

  }


  addFoodItem(foodId: string) {
    for(let i = 0; i < this.foods.length; i++){
      if (foodId === this.foods[i].ID){
        this.selectedFood.push(this.foods[i].FOOD_NAME)
      }
    }
  }

  removeFoodItem(index: number) {
    this.selectedFood.splice(index, 1);
  }

  removeAllFood() {
    this.selectedFood = []
  }

  onSubmitIngredients() {

    console.log('I want a recipe with the following ingredients: ')
  }


}




<br>
<div id="container-search-food">
      <div>
            <input id="food-input" (keydown)="onKeyPressMove($event)" placeholder="Search Food" >
            <button type="submit">Add</button>
      </div>
      <div id="container-list-display" *ngFor="let food of foods">
            <ul id="unordered-list-for-selection">
                  <li  id="food-list-item" 
                        (click)="addFoodItem(food.ID)" 
                        value="{{ food.FOOD_NAME }}">
                        {{ food.FOOD_NAME }}
                  </li>
            </ul>
      </div> 

</div>
<div id="container-selected-food">
      <h6 id="selected-food-title">Selected food:</h6>

      <div *ngFor="let food of selectedFood; let i = index">
            <ul id="unordered-list-selected">
                  <li id="selected-food-item-list">
                        <p id="selected-item-paragraph">{{ food }}</p>
                        <span (click)="removeFoodItem(i)" 
                              id="cancel-food-button">
                              &#x2715;
                        </span>
                  </li>
            </ul>
      </div>      
      <button id="submit-food-button" (click)="onSubmitIngredients()">Submit</button>

      <button id="clear-all-food-button" (click)="removeAllFood()">Clear all</button>

</div>
